// Check if we have valid data
if (!msg.payload || !msg.payload.main || !msg.payload.weather) {
    node.error("Invalid weather data received");
    return null;
}

// Extract and format data
const weather = msg.payload;

// Current time
const now = new Date();
const hours = now.getHours();
const minutes = now.getMinutes();
const timeStr = hours + ':' + (minutes < 10 ? '0' : '') + minutes;

// Round temperature to nearest integer
const temperature = Math.round(weather.main.temp);

// Store all data in flow context
flow.set('temperature', temperature + '째C');
flow.set('humidity', weather.main.humidity + '%');
flow.set('wind', Math.round(weather.wind.speed) + ' km/h');

// Ensure we have weather description
let description = "Unknown";
if (weather.weather && weather.weather.length > 0 && weather.weather[0].description) {
    description = weather.weather[0].description;
}
flow.set('description', description);

// Store city name and current time
flow.set('city', weather.name + ' ' + timeStr);

// Store current time separately for time display
flow.set('current_time', timeStr);

// Set default display mode if not set
if (!flow.get('display_mode')) {
    flow.set('display_mode', 'temperature');
}

// Get switches mode - check if switches have changed
const switchesMode = flow.get('switches_mode') || 'time';

// Send current switches mode to the display handler
msg.payload = switchesMode;

// Add last_updated timestamp to track successful updates
flow.set('last_updated', Date.now());

return msg;

// Get last update timestamp
const lastUpdated = flow.get('last_updated') || 0;
const now = Date.now();

// If no update in the last 20 minutes (1200000ms), force refresh
if (now - lastUpdated > 1200000) {
    node.warn("No weather updates for 20+ minutes. Forcing refresh.");
    // Set payload to trigger a new weather fetch
    msg.payload = Date.now();
    return msg;
}

// Otherwise no need to do anything
return null;

// Get current switches mode
const switchesMode = flow.get('switches_mode') || 'time';

// DEBUG mode
node.log("DISPLAY HANDLER - Current mode: " + switchesMode);

// Get weather display mode (only applies when in weather mode)
const weatherMode = flow.get('weather_mode') || 'temperature';

// Get data from flow context
const temperature = flow.get('temperature') || 'N/A';
const humidity = flow.get('humidity') || 'N/A';
const wind = flow.get('wind') || 'N/A';
const description = flow.get('description') || 'N/A';
const city = flow.get('city') || 'Connecting...';

// Time-specific data
const now = new Date();
const hours = now.getHours();
const minutes = now.getMinutes();
const seconds = now.getSeconds();
const timeStr = hours + ':' + (minutes < 10 ? '0' : '') + minutes + ':' + (seconds < 10 ? '0' : '') + seconds;
const dateStr = now.toLocaleDateString('de-DE');

// Clean function to remove/replace problematic characters
function cleanText(text) {
    if (typeof text !== 'string') return 'N/A';

    // Replace HTML entities and problematic characters
    return text
        .replace(/&#x2F;/g, '|')         // Replace HTML entity for slash
        .replace(/&#(\d+);/g, '')        // Remove all other HTML entities
        .replace(/[^\x20-\x7E]/g, '')    // Keep only basic ASCII characters
        .replace(/[^\w\s.,째%\/-:]/g, ''); // Keep only alphanumeric, spaces, and some symbols
}

// Ensure values are strings and limit to display width (16 chars for standard LCD)
function formatForDisplay(text, maxLength = 16) {
    let cleaned = cleanText(String(text));
    return cleaned.length > maxLength ? cleaned.substring(0, maxLength) : cleaned;
}

// Clean all displayed text
const cleanCity = formatForDisplay(city);
const cleanTemp = formatForDisplay(temperature);
const cleanHumidity = formatForDisplay(humidity);
const cleanWind = formatForDisplay(wind);
const cleanTimeStr = formatForDisplay(timeStr);
const cleanDateStr = formatForDisplay(dateStr);

// Special formatting for description - capitalize first letter
let cleanDescription = formatForDisplay(description);
if (cleanDescription.length > 0) {
    cleanDescription = cleanDescription.charAt(0).toUpperCase() + cleanDescription.slice(1);
}

// Prepare messages for line 1 and 2
let line1;
let line2;

// Mode 1: Time display (both switches OFF)
if (switchesMode === 'time') {
    line1 = { payload: "Datum : "+cleanDateStr, line: 1 };
    line2 = { payload:  cleanTimeStr, line: 2 };
    node.log("Display set to TIME mode");
}
// Mode 2: Weather display (switch 1 ON, switch 2 OFF)
else if (switchesMode === 'weather') {
    // Sub-modes for weather display based on weatherMode
    node.log("Display set to WEATHER mode, sub-mode: " + weatherMode);
    switch(weatherMode) {
        case 'temperature':
            line1 = { payload: "Temperatur", line: 1 };
            line2 = { payload: cleanTemp, line: 2 };
            break;
        case 'humidity':
            line1 = { payload: "Luftfeuchtigkeit", line: 1 };
            line2 = { payload: cleanHumidity, line: 2 };
            break;
        case 'wind':
            line1 = { payload: "Windgeschwindigk.", line: 1 };
            line2 = { payload: cleanWind, line: 2 };
            break;
        case 'description':
            line1 = { payload: "Wetterlage", line: 1 };
            line2 = { payload: cleanDescription, line: 2 };
            break;
        default:
            line1 = { payload: "Temperatur", line: 1 };
            line2 = { payload: cleanTemp, line: 2 };
    }
}
// Mode 3: System info (switch 1 OFF, switch 2 ON)
else if (switchesMode === 'system') {
    // Get uptime using 'uptime' command
    node.log("Display set to SYSTEM mode");
    //const execSync = require('child_process').execSync;
    let systemInfo;
    try {
        // Try to get system info
        systemInfo = "System aktiv";
    } catch (e) {
        systemInfo = "System Info N/A";
    }

    line1 = { payload: "System Info", line: 1 };
    line2 = { payload: formatForDisplay(systemInfo), line: 2 };
}

// Mode 4: Custom message (both switches ON)
else if (switchesMode === 'custom') {
    node.log("Display set to CUSTOM mode");
    line1 = { payload: "Benutzerdefiniert", line: 1 };
    line2 = { payload: "Hallo Welt!", line: 2 };
}
// Default fallback to time display
else {
    node.warn("Unknown mode: " + switchesMode + ", defaulting to TIME");
    line1 = { payload: "Datum", line: 1 };
    line2 = { payload: cleanDateStr + " " + cleanTimeStr, line: 2 };
}

// Return both messages as array
return [line1, line2];

// Only process when button is pressed (rising edge)
if (msg.payload !== 1) return null;

// Only cycle weather modes if we're in weather display mode
const switchesMode = flow.get('switches_mode') || 'time';
if (switchesMode !== 'weather') {
    // We're not in weather mode, so button does nothing
    return null;
}

// Get current mode
const currentMode = flow.get('weather_mode') || 'temperature';

// Define the cycle order - limited to just 4 essential modes
const modes = ['temperature', 'humidity', 'wind', 'description'];

// Track button press count to prevent excessive cycling
let pressCount = flow.get('button_press_count') || 0;
pressCount++;

// Store press count for debouncing purposes
flow.set('button_press_count', pressCount);
flow.set('last_button_press', Date.now());

// Find current index
let currentIndex = modes.indexOf(currentMode);

// If current mode not found in array, default to first mode
if (currentIndex === -1) currentIndex = 0;

// Move to next mode (or back to first if at end)
// This ensures limited cycling through only 4 modes
let nextIndex = (currentIndex + 1) % modes.length;

// Set the next mode
const nextMode = modes[nextIndex];
flow.set('weather_mode', nextMode);
msg.payload = nextMode;

// Also log mode change
node.log("Weather display mode changed to: " + nextMode + " (press #" + pressCount + ")");

// Return the message with the new mode
return msg;

// Process input from GPIO pins
// Check if the message has pin information in the topic
const isPinTopic = msg.topic && (msg.topic.includes("20") || msg.topic.includes("21"));

// Store switch states based on pin number or explicit topic
if (msg.topic === 'schalter1' || (isPinTopic && msg.topic.includes("21"))) {
    // For pull-up resistors: 0 = pressed/ON, 1 = not pressed/OFF
    // If using pull-up, logical state is inverse of electrical state
    flow.set('switch1', msg.payload === 0 ? 1 : 0);
    node.log("Switch 1 set to: " + flow.get('switch1') + " (GPIO: " + msg.payload + ")");
} else if (msg.topic === 'schalter2' || (isPinTopic && msg.topic.includes("20"))) {
    flow.set('switch2', msg.payload === 0 ? 1 : 0);
    node.log("Switch 2 set to: " + flow.get('switch2') + " (GPIO: " + msg.payload + ")");
} else {
    node.log("Unknown switch input: " + JSON.stringify({topic: msg.topic, payload: msg.payload}));
}

// Get current switch states
const switch1 = flow.get('switch1') || 0;
const switch2 = flow.get('switch2') || 0;

// Determine display mode based on switch positions
let mode;
if (switch1 === 0 && switch2 === 0) mode = 'time';       // Both OFF
else if (switch1 === 1 && switch2 === 0) mode = 'weather';  // Switch 1 ON
else if (switch1 === 0 && switch2 === 1) mode = 'system';   // Switch 2 ON
else if (switch1 === 1 && switch2 === 1) mode = 'custom';   // Both ON
else {
    mode = 'time';  // Default fallback
    node.warn("Invalid switch values: S1=" + switch1 + ", S2=" + switch2);
}

// Update mode and return message
flow.set('switches_mode', mode);
return { payload: mode, topic: 'mode_change' };

initialize:
// Initialize switch states
flow.set('switch1', 0);
flow.set('switch2', 0);
flow.set('switches_mode', 'time');
node.log("Switch states initialized");

